import { EntityService, type IEntitiesService } from '@kinotic/structures-api'
import { Alert } from '../../domain/Alert.js'


/**
 * Base Service for interacting with Alert entities
 * This class was generated by the Structures CLI. And will be overwritten if the CLI is run again.
 */
export class BaseAlertEntityService extends EntityService<Alert> {

  private readonly shouldValidate: boolean

  constructor(shouldValidate: boolean = true, entitiesService?: IEntitiesService) {
    super('fvnoKFXGlQ', 'Alert', entitiesService)
    this.shouldValidate = shouldValidate
  }

  protected async beforeSaveOrUpdate(entity: Alert): Promise<Alert> {
    if (this.shouldValidate) {
      return this.validate(entity)
    } else {
      return entity
    }
  }

  protected async beforeBulkSaveOrUpdate(entities: Alert[]): Promise<Alert[]> {
    if (this.shouldValidate) {
      const validatedEntities: Alert[] = []
      for (let entity of entities) {
        validatedEntities.push(this.validate(entity))
      }
      return validatedEntities
    } else {
      return entities
    }
  }

  validate(entity: Alert): Alert {
    let ret: any
    if (entity) {
      ret = (ret ? ret : {})
      ret.alertId = entity.alertId
      ret.message = entity.message
      ret.severity = entity.severity
      ret.timestamp = entity.timestamp
      ret.source = entity.source
      ret.active = entity.active
    }

    return ret
  }

}
